html:
{% set items=frappe.get_list('Sales Invoice Item',filters={'parent':doc.name},fields=['*'])%}
{% set currency=frappe.db.get_value('Currency',doc.currency,'symbol')%}

<style>
    .print-format{
padding: 20px;
    /*background-image: url(https://electra/files/body.jpg);*/
    background-size: contain;
    background-repeat: no-repeat;position:relative;
    background-position-x: center;
} 
</style>
<div class="p-format" id="wrapper">
    <div>
        {% set letter_head=frappe.get_list('Letter Head',filters={'is_default':1},fields=['content'])%}
        {% if doc.company%}

        <div>
            <div class="row" style="">
                <div style="text-align:center;">
                   <img src="{{frappe.get_url()}}/files/Screenshot from 2022-05-16 12-39-47.png" style="padding: 0px 14px 0 14px;"/>
                </div>
            </div>
            <div class="row">
                <div class="cols-4 col-md-4">
                    <img src="/assets/electra/images/torquefavi.png" class="img-style" style="display:none;" />
                </div>
                <div class="cols-4 col-md-4" style="">
                    <h4 style="text-align:center;font-size: 15px;">SALES INVOICE</h4>
                </div>
                <div class="cols-4 col-md-4" style="text-align:right;">
                    <h4 style="text-transform: uppercase;margin-bottom: 0;">{{doc.company}}</h4>
                     {% if doc.company_address %}
                    {% set address=frappe.get_doc('Address',doc.company_address) %}
                    {% if address %}
                    <p style="font-size: 13px;">
                        {{address.address_line1}}, {% if address.address_line2 %}
                        <br>
                        {{address.address_line2}},{% endif %}
                        <br>
                        {{address.city}}, {{address.state}}-{{address.pincode}}
                        <br>
                        {% if address.pan_no %}PAN NO :{{address.pan_no}} <br>{% endif %}{% if address.gstin %}GSTIN :{{address.gstin}}{% endif %}
                    </p>
                    {% endif %}
                    {% endif %}
                </div>
            </div>
        </div>
        {% endif %}
    </div>
    <div class="content">
        <table style="width:100%;">
            <tr style="">
                <td style="width:70%;"><b>Invoice No: </b> {{doc.name}}</td>
                <td  style="text-align:right;"><b>Invoice Date:</b> {{doc.get_formatted("posting_date")}}</td>
            </tr>
        </table>
        <div class="row controw">
            <div class="cols-8 col-md-8">
                <h6 style="margin-left:10px;"><b>Customer Name & Address</b></h6>
                <div style="margin-left: 20px;">
                    <p><b>{{doc.customer_name}}</b></p>
                    {% if doc.customer_address %}{% set cust_address=frappe.get_doc('Address',doc.customer_address) %} {% endif %}
                    {% if cust_address %}
                    <div>
                        {{cust_address.address_line1}}, {% if cust_address.address_line2 %}
                        <br>
                        {{cust_address.address_line2}},{% endif %}
                        <br>
                        {{cust_address.city}}, {{cust_address.state}}-{{cust_address.pincode}}
                        <br>
                        <b>{% if cust_address.gstin %}GSTIN :{{cust_address.gstin}}{% endif %}
                        {% if cust_address.gst_state_number %}GST State No: {{cust_address.gst_state_number}}{% endif %}</b>
                    </div>
                    {% endif %}
                </div>
            </div>
            <div class="cols-4 col-md-4">
                <table>

                    {%if doc.vehicle_type%}
                    <tr>
                        <td><b>Transportation:</b></td>
                        <td>{% if doc.vehicle_type %}{{doc.vehicle_type}}{% endif %}</td>
                    </tr>
                    <tr>
                        <td><b>Truck No:</b></td>
                        <td>{% if doc.manually_check %}{% if doc.vehicle_no %}{{doc.vehicle_no}}{% endif %}{% else %}{% if doc.vehicle %}{{doc.vehicle}}{% endif %}{% endif %}</td>
                    </tr>
                    <tr>
                        <td><b>Transporter Name:</b></td>
                        <td>{% if doc.transporter %}{{doc.transporter}}{% else %}NA{% endif %}</td>
                    </tr>
                    {%endif%}
                </table>
            </div>
        </div>


        <table class="table table-bordered" style="margin-top: 5px;">
            <thead>
                <tr>
                    <th >S.No</th>
                    <th >Item Name</th>

                    <th >Qty ({{doc.items[0].uom}})</th>
                    <th >Unit Price</th>
                    <th >Discount per Qty</th>
                    <th >Taxable Price/Qty</th>
                    <th >Taxable Value</th>
                    {% if doc.taxes %}{% for item in doc.taxes %}
                    <th >{{item.description.split('@')[0]}} Rate</th>
                    <th >{{item.description.split('@')[0]}} Amount</th>
                    {% endfor %}{% endif %}
                    <th >Total Amount</th>
                </tr>
            </thead>
            <tbody>
                {% if doc.items %}

    {% for group in doc.items  %}
     {% set item_code =  group.item_code %}
            {% set item_name =  group.item_name%}

            {% set tot_qty = group.qty %}
            {% set price_list_rate = group.price_list_rate %}
            {% set discount = 0 %}
            {% set rate =  group.rate %}
             {% set amount =  group.amount %}
            {% set tot_amt = group.net_amount %}

                <tr>
                    <td>{{loop.index}}</td>

                  <td style="width:15%;">{{item_name}}</td>

                    <td style="text-align:right;">{{tot_qty}}</td>
                    <td style="text-align:right;">{{"%0.2f" % price_list_rate | float}}</td>
                    <td style="text-align:right;width:5%;">{% if discount %}{{"%0.2f" % discount | float}}{% else %}0.00{% endif %}</td>
                    <td style="text-align:right;">{{"%0.2f" % rate | float}}</td>
                    <td style="text-align:right;">{{"%0.2f" % amount | float}}</td>
                       {% if doc.taxes %}{% for tax in doc.taxes %}
                   {{string_to_json(json_string=tax.item_wise_tax_detail)}}
                  {% set rate = string_to_json(json_string=tax.item_wise_tax_detail)[item_code[0]][0] | json %}
                    <td style="text-align:right;">{{"%0.2f" % rate | float}}</td>
                    <td style="text-align:right;">{{"%0.2f" % tax.tax_amount | float}}</td>
                    {% endfor%}{% endif %}

                    <td style="text-align:right;">{{"%0.2f" % tot_amt | float}}</td>
                </tr>
                {% endfor %}
                {% endif %}
            </tbody>

        </table>

        <div>

        </div>

    </div>
    <div class="page-break" >
    <div  class="bot">
        <table class="table table-bordered" style="margin-top: 5px;margin-bottom: 10px;">
                <tfoot>
                <tr>
                    <td colspan="5"><b>Invoice value in words: {{doc.in_words}}</b></td>
                       <td colspan="2"><b>Total Qty: {{doc.total_qty}}</b></td>
                    {% if doc.taxes %}{% set cols=doc.taxes | length * 2 +1 %}{% else %}{% set cols=5 %}{% endif %}
                    <td colspan="{{cols}}"><b>Total: <span style="float:right;">{{currency}} {% if doc.rounded_total %}{{"%.2f" % doc.rounded_total | float}}{% else %}{{"%.2f" % doc.grand_total | float}}{% endif %}</span></b></td>
                </tr>
            </tfoot>
                </table>
            <div class="row" style="">
                <div style="text-align:center;">
                   <img src="{{frappe.get_url()}}/files/Screenshot from 2022-05-16 12-44-34.png" style="padding: 0px 14px 0 14px;"/>
                </div>
            </div>
    </div>
</div>
</div>


<script>

    // $('.print-format').append('<div id="footer-html tri" class="visible-pdf" style="display: block !important;order: 1;margin-top: auto;"><div class="letter-head-footer"><img src="/files/footer.jpg" style="width: 100%;"></div><p class="text-center small page-number visible-pdf">Page <span class="page"></span> of <span class="topage"></span></p></div>');
</script>
